import { moduleMetadata, type Meta, type StoryObj } from '@storybook/angular';
import { RippleComponent } from '../ripple.component';
import { CommonModule } from '@angular/common';
import { RippleWave } from '@services/ripple.service';

// More on how to set up stories at: https://storybook.js.org/docs/writing-stories
const meta: Meta<RippleComponent> = {
  title: 'Utils/Ripple',
  component: RippleComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [CommonModule, RippleComponent],
    }),
  ],
};

export default meta;
type Story = StoryObj<RippleComponent>;

interface RippleDemoProps {
  waves: RippleWave[];
  addRipple: (event: MouseEvent) => void;
}

// Demo component with ripple effect simulation
export const Preview: Story = {
  render: () => ({
    styles: [`
      .ripple-demo {
        position: relative;
        width: 200px;
        height: 200px;
        background-color: var(--tgui--secondary_fill);
        border-radius: 8px;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        overflow: hidden;
        margin-bottom: 16px;
      }

      .ripple-demo::after {
        content: 'Click for demonstration';
        position: relative;
        z-index: 1;
      }

      .description {
        max-width: 600px;
        margin-bottom: 16px;
      }
    `],
    template: `
      <div class="description">
        <p>Ripple component creates a wave effect when an interactive element is pressed.
           This effect is automatically used in the Button component with interactiveAnimation="background" value.</p>
        <p>Ripple effect is displayed only on non-iOS platforms as it does not comply with iOS design guidelines.</p>
      </div>

      <div #container class="ripple-demo" (click)="addRipple($event)">
        <tgui-ripple [waves]="waves"></tgui-ripple>
      </div>
    `,
    props: {
      waves: [] as RippleWave[],
      addRipple: function(event: MouseEvent) {
        const rect = (event.currentTarget as HTMLElement).getBoundingClientRect();
        const x = event.clientX - rect.left;
        const y = event.clientY - rect.top;

        const newWave: RippleWave = {
          x,
          y,
          date: Date.now(),
          pointerId: Date.now() // use timestamp as a unique ID
        };

        this['waves'] = [...this['waves'], newWave];

        // Clear the wave after animation completes
        setTimeout(() => {
          this['waves'] = this['waves'].filter((wave: RippleWave) => wave.date !== newWave.date);
        }, 300);
      }
    } as RippleDemoProps
  }),
};

// Usage description
export const Usage: Story = {
  render: () => ({
    template: `
      <div style="max-width: 700px;">
        <h3>How to use Ripple</h3>
        <p>The Ripple component is usually not used directly in developer code, 
        but is integrated into buttons and other interactive elements.</p>

        <h4>Example of usage in the Button component:</h4>
        <pre>
&lt;tgui-button interactiveAnimation="background"&gt;
  With ripple effect
&lt;/tgui-button&gt;
        </pre>

        <p>The Ripple component is automatically displayed only on non-iOS platforms and
        when the button has the parameter interactiveAnimation="background".</p>
      </div>
    `,
  }),
};

import { Injectable, signal } from '@angular/core';

export interface RippleWave {
  x: number;
  y: number;
  date: number;
  pointerId: number;
}

@Injectable({
  providedIn: 'root'
})
export class RippleService {
  private RIPPLE_DELAY = 70;
  private WAVE_LIVE = 225;

  /**
   * Map to track pointer delay timers by pointerId
   */
  private pointerDelayTimers = new Map<number, ReturnType<typeof setTimeout>>();

  /**
   * Add a new ripple wave effect at the specified coordinates
   */
  addWave(x: number, y: number, pointerId: number, currentWaves: RippleWave[]): RippleWave[] {
    const dateNow = Date.now();
    // Filter out expired waves
    const filteredWaves = currentWaves.filter((wave) => wave.date + this.WAVE_LIVE > dateNow);

    // Add the new wave
    const newWaves = [
      ...filteredWaves,
      {
        x,
        y,
        date: dateNow,
        pointerId,
      }
    ];

    // Clean up the timer for this pointerId
    this.pointerDelayTimers.delete(pointerId);

    return newWaves;
  }

  /**
   * Handle pointer down event
   * @param event Pointer event
   * @param wavesSignal Signal для управления волнами
   */
  handlePointerDown(event: PointerEvent, wavesSignal: ReturnType<typeof signal<RippleWave[]>>): void {
    const rect = (event.currentTarget as HTMLElement).getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Set a timeout to create the ripple effect after a short delay
    this.pointerDelayTimers.set(
      event.pointerId,
      setTimeout(() => {
        const newWaves = this.addWave(x, y, event.pointerId, wavesSignal());
        wavesSignal.set(newWaves);

        // Clear the waves after they've completed their animation
        setTimeout(() => {
          wavesSignal.set([]);
        }, this.WAVE_LIVE);
      }, this.RIPPLE_DELAY)
    );
  }

  /**
   * Handle pointer cancel/up event
   */
  handlePointerCancel(pointerId: number): void {
    const timer = this.pointerDelayTimers.get(pointerId);
    if (timer) {
      clearTimeout(timer);
      this.pointerDelayTimers.delete(pointerId);
    }
  }
}

import { Injectable, inject, TemplateRef, createComponent, ApplicationRef, EnvironmentInjector } from '@angular/core';
import { SnackbarComponent } from './snackbar.component';

export interface SnackbarOptions {
  beforeTemplate?: TemplateRef<any>;
  afterTemplate?: TemplateRef<any>;
  description?: string;
  linkTemplate?: TemplateRef<any>;
  duration?: number;
}

@Injectable({
  providedIn: 'root'
})
export class SnackbarService {
  private appRef = inject(ApplicationRef);
  private injector = inject(EnvironmentInjector);
  private activeSnackbars: any[] = [];

  show(message: string, options: SnackbarOptions = {}): void {
    // Создаем компонент программно
    const snackbarRef = createComponent(SnackbarComponent, {
      environmentInjector: this.injector,
      projectableNodes: [[document.createTextNode(message)]]
    });

    // Настраиваем свойства
    const instance = snackbarRef.instance;
    instance.beforeTemplate = options.beforeTemplate;
    instance.afterTemplate = options.afterTemplate;
    instance.description = options.description;
    instance.linkTemplate = options.linkTemplate;
    instance.duration = options.duration ?? 4000;
    instance.onClose = () => this.close(snackbarRef);

    // Добавляем в DOM и в отслеживаемые
    this.appRef.attachView(snackbarRef.hostView);
    this.activeSnackbars.push(snackbarRef);
  }

  private close(snackbarRef: any): void {
    const index = this.activeSnackbars.indexOf(snackbarRef);
    if (index !== -1) {
      this.activeSnackbars.splice(index, 1);
      this.appRef.detachView(snackbarRef.hostView);
      snackbarRef.destroy();
    }
  }

  closeAll(): void {
    for (const snackbar of this.activeSnackbars) {
      snackbar.instance.close();
    }
  }
}

import { EventEmitter } from '@angular/core';

/**
 * Base interface for icon components, compatible with React version
 */
export interface TguiIconProps {
  /**
   * CSS class to apply to the icon
   */
  class?: string;

  /**
   * Additional styles to apply to the icon
   */
  style?: { [key: string]: string };

  /**
   * Aria label for accessibility
   */
  ariaLabel?: string;

  /**
   * Title for the icon
   */
  title?: string;

  /**
   * ID of the element
   */
  id?: string;

  /**
   * Fill color of the icon
   */
  fill?: string;

  /**
   * Stroke color of the icon
   */
  stroke?: string;

  /**
   * Width of the icon (can be CSS units or number)
   */
  width?: string | number;

  /**
   * Height of the icon (can be CSS units or number)
   */
  height?: string | number;

  /**
   * Role attribute for accessibility
   */
  role?: string;

  /**
   * Tab index for keyboard navigation
   */
  tabIndex?: number;

  /**
   * Whether the element should get focus on load
   */
  autoFocus?: boolean;

  /**
   * Click event handler
   */
  onClick?: EventEmitter<MouseEvent>;

  /**
   * Focus event handler
   */
  onFocus?: EventEmitter<FocusEvent>;

  /**
   * Blur event handler
   */
  onBlur?: EventEmitter<FocusEvent>;

  /**
   * Mouse enter event handler
   */
  onMouseEnter?: EventEmitter<MouseEvent>;

  /**
   * Mouse leave event handler
   */
  onMouseLeave?: EventEmitter<MouseEvent>;

  /**
   * Key down event handler
   */
  onKeyDown?: EventEmitter<KeyboardEvent>;

  /**
   * Pointer down event handler
   */
  onPointerDown?: EventEmitter<PointerEvent>;

  /**
   * Pointer up event handler
   */
  onPointerUp?: EventEmitter<PointerEvent>;
}

import { Injectable } from '@angular/core';

// Define Telegram WebApp interface
interface ThemeParams {
  bg_color?: string;
  text_color?: string;
  hint_color?: string;
  link_color?: string;
  button_color?: string;
  button_text_color?: string;
  secondary_bg_color?: string;
  header_bg_color?: string;
  accent_text_color?: string;
  section_bg_color?: string;
  section_header_text_color?: string;
  subtitle_text_color?: string;
  destructive_text_color?: string;
}

interface WebApp {
  colorScheme: 'light' | 'dark';
  themeParams: ThemeParams;
  onEvent(eventName: string, eventHandler: () => void): void;
  offEvent(eventName: string, eventHandler: () => void): void;
}

declare global {
  interface Window {
    Telegram?: {
      WebApp?: WebApp;
    };
  }
}

@Injectable({
  providedIn: 'root'
})
export class TelegramService {
  // Check if DOM is available
  private get canUseDOM(): boolean {
    return !!(typeof window !== 'undefined' && window.document && window.document.createElement);
  }

  // Get Telegram WebApp data
  public getTelegramData(): WebApp | undefined {
    if (!this.canUseDOM) {
      return undefined;
    }

    return window.Telegram?.WebApp;
  }

  // Helper function to convert hex color to RGB
  public hexToRGB(hex: string): [number, number, number] {
    // Remove # if present
    hex = hex.replace('#', '');

    // Parse hex values
    const r = parseInt(hex.substring(0, 2), 16);
    const g = parseInt(hex.substring(2, 4), 16);
    const b = parseInt(hex.substring(4, 6), 16);

    return [r, g, b];
  }
}

