import { Injectable, OnDestroy, inject, signal } from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { AppearanceType } from '@services/theme.service';

/**
 * Apply the specified theme by adding the appropriate CSS class to the document's root element
 * @param theme The theme to apply ('light' or 'dark')
 */
export function applyTheme(theme: AppearanceType): void {
  // Remove existing theme classes
  document.documentElement.classList.remove('tgui-theme-light', 'tgui-theme-dark');

  // Add the appropriate theme class
  document.documentElement.classList.add(`tgui-theme-${theme}`);
}

/**
 * Setup system theme detection using prefers-color-scheme
 * @param callback Optional callback function that will be called when the theme changes
 * @returns A function to cleanup the listeners
 */
export function setupSystemThemeDetection(callback?: (theme: AppearanceType) => void): () => void {
  if (typeof window === 'undefined') return () => {};

  const isDarkMode = window.matchMedia('(prefers-color-scheme: dark)');

  // Apply initial theme based on system preference
  const initialTheme: AppearanceType = isDarkMode.matches ? 'dark' : 'light';
  applyTheme(initialTheme);
  callback?.(initialTheme);

  // Add listener for theme changes
  const handleThemeChange = (event: MediaQueryListEvent) => {
    const newTheme: AppearanceType = event.matches ? 'dark' : 'light';
    applyTheme(newTheme);
    callback?.(newTheme);
  };

  isDarkMode.addEventListener('change', handleThemeChange);

  // Return cleanup function
  return () => {
    isDarkMode.removeEventListener('change', handleThemeChange);
  };
}

/**
 * Angular service для использования системных тем
 * Использует сигналы для отслеживания изменений темы
 */
@Injectable({
  providedIn: 'root'
})
export class SystemThemeService implements OnDestroy {
  // Signal для темы с начальным значением
  private readonly themeSignal = signal<AppearanceType>(this.getInitialTheme());

  // Публичный readonly signal для доступа к теме
  public readonly theme = this.themeSignal.asReadonly();

  private cleanup: (() => void) | null = null;

  constructor() {
    this.setupThemeDetection();
  }

  // Устанавливаем тему вручную
  setTheme(theme: AppearanceType): void {
    this.themeSignal.set(theme);
    applyTheme(theme);
  }

  // Включаем отслеживание системной темы
  enableSystemTheme(): void {
    this.cleanup && this.cleanup();
    this.setupThemeDetection();
  }

  // Отключаем отслеживание системной темы
  disableSystemTheme(): void {
    this.cleanup && this.cleanup();
    this.cleanup = null;
  }

  ngOnDestroy(): void {
    this.cleanup && this.cleanup();
  }

  private getInitialTheme(): AppearanceType {
    if (typeof window === 'undefined') return 'light';

    const isDarkMode = window.matchMedia('(prefers-color-scheme: dark)');
    return isDarkMode.matches ? 'dark' : 'light';
  }

  private setupThemeDetection(): void {
    if (typeof window === 'undefined') return;

    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');

    // Устанавливаем начальную тему
    const initialTheme: AppearanceType = mediaQuery.matches ? 'dark' : 'light';
    this.themeSignal.set(initialTheme);
    applyTheme(initialTheme);

    // Создаем обработчик события изменения медиа-запроса
    const handleMediaQueryChange = (event: MediaQueryListEvent) => {
      const newTheme: AppearanceType = event.matches ? 'dark' : 'light';
      this.themeSignal.set(newTheme);
      applyTheme(newTheme);
    };

    // Добавляем слушатель события
    mediaQuery.addEventListener('change', handleMediaQueryChange);

    // Функция очистки
    this.cleanup = () => {
      mediaQuery.removeEventListener('change', handleMediaQueryChange);
    };
  }
}

import { Injectable } from '@angular/core';

// Define Telegram WebApp interface
interface ThemeParams {
  bg_color?: string;
  text_color?: string;
  hint_color?: string;
  link_color?: string;
  button_color?: string;
  button_text_color?: string;
  secondary_bg_color?: string;
  header_bg_color?: string;
  accent_text_color?: string;
  section_bg_color?: string;
  section_header_text_color?: string;
  subtitle_text_color?: string;
  destructive_text_color?: string;
}

interface WebApp {
  colorScheme: 'light' | 'dark';
  themeParams: ThemeParams;
  onEvent(eventName: string, eventHandler: () => void): void;
  offEvent(eventName: string, eventHandler: () => void): void;
}

declare global {
  interface Window {
    Telegram?: {
      WebApp?: WebApp;
    };
  }
}

@Injectable({
  providedIn: 'root'
})
export class TelegramService {
  // Check if DOM is available
  private get canUseDOM(): boolean {
    return !!(typeof window !== 'undefined' && window.document && window.document.createElement);
  }

  // Get Telegram WebApp data
  public getTelegramData(): WebApp | undefined {
    if (!this.canUseDOM) {
      return undefined;
    }

    return window.Telegram?.WebApp;
  }

  // Helper function to convert hex color to RGB
  public hexToRGB(hex: string): [number, number, number] {
    // Remove # if present
    hex = hex.replace('#', '');

    // Parse hex values
    const r = parseInt(hex.substring(0, 2), 16);
    const g = parseInt(hex.substring(2, 4), 16);
    const b = parseInt(hex.substring(4, 6), 16);

    return [r, g, b];
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class TguiService {

  constructor() { }
}

import { DOCUMENT } from '@angular/common';
import { Inject, Injectable, OnDestroy, signal, inject, Renderer2, RendererFactory2 } from '@angular/core';
import { TelegramService } from './telegram.service';

export type AppearanceType = 'light' | 'dark';

@Injectable({
  providedIn: 'root'
})
export class ThemeService implements OnDestroy {
  // Signal for appearance that components can subscribe to
  public appearance = signal<AppearanceType>('light');
  private themeChangeListener: (() => void) | null = null;
  private mediaQueryList: MediaQueryList | null = null;
  private handleThemeChange: ((event: MediaQueryListEvent) => void) | null = null;
  private useSystemTheme = false;
  private currentTheme: AppearanceType = 'light';
  private renderer: Renderer2;

  private telegramService = inject(TelegramService);
  private document = inject(DOCUMENT);

  constructor(rendererFactory: RendererFactory2) {
    console.log(`themeService: constructor`);
    this.renderer = rendererFactory.createRenderer(null, null);
    this.initializeTheme();
  }

  ngOnDestroy(): void {
    this.cleanupListeners();
  }

  /**
   * Change the theme manually
   * @param theme The theme to set
   * @param followSystem If true, will follow system theme changes after setting. Default false.
   */
  public setTheme(theme: AppearanceType, followSystem = false): void {
    this.cleanupListeners();
    this.appearance.set(theme);
    this.applyThemeToDOM(theme);

    // If instructed to follow system theme, restore the detector
    if (followSystem) {
      this.useSystemTheme = true;
      this.setupBrowserThemeDetection();
    } else {
      this.useSystemTheme = false;
    }
  }

  /**
   * Setup theme based on inputs and system preferences
   * @param appearance Appearance to use
   * @param followSystem Whether to follow system theme
   */
  public setupTheme(appearance?: AppearanceType, followSystem: boolean = false): void {
    console.log(`themeService: setupTheme: appearance=${appearance}, currentTheme=${this.currentTheme}`);
    this.cleanupListeners();
    if (followSystem) {
      // Follow system theme
      this.useSystemTheme = true;
      this.setupBrowserThemeDetection();
    } else if (appearance) {
      // Set specific theme
      this.setTheme(appearance, false);
    } else {
      // If nothing specified, try to detect system theme
      this.detectSystemTheme();
    }
  }

  /**
   * Detect and apply system theme
   */
  public detectSystemTheme(): void {
    if (typeof window === 'undefined') {
      // Use light theme by default
      this.setTheme('light', false);
      return;
    }

    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    this.setTheme(prefersDarkMode ? 'dark' : 'light', false);
  }

  /**
   * Load global CSS variables to make them available for all components
   */
  public loadGlobalStyles(): void {
    // Check if styles are already loaded
    if (this.document.getElementById('tgui-variables-css')) {
      return;
    }

    const head = this.document.head;
    const link = this.document.createElement('link');

    link.id = 'tgui-variables-css';
    link.rel = 'stylesheet';
    link.type = 'text/css';

    // Add error handling
    link.onerror = () => {
      console.error('Failed to load TGUI variables CSS file. Theme functionality may be limited.');
    };

    // In production builds, this will be replaced with the actual path
    // The actual file is bundled with the library during build
    link.href = 'assets/tgui/styles/variables.css';

    head.appendChild(link);
  }

  /**
   * Initialize the theme detection
   */
  private initializeTheme(): void {
    // First check Telegram API
    const telegramData = this.telegramService.getTelegramData();

    if (telegramData) {
      // Use Telegram theme
      this.appearance.set(telegramData.colorScheme);
      this.applyThemeToDOM(telegramData.colorScheme);

      // Set up listener for theme changes
      this.themeChangeListener = () => {
        const newTelegramData = this.telegramService.getTelegramData();
        if (newTelegramData) {
          this.appearance.set(newTelegramData.colorScheme);
          this.applyThemeToDOM(newTelegramData.colorScheme);
        }
      };

      telegramData.onEvent('themeChanged', this.themeChangeListener);
    } else {
      // Use browser preference
      this.useSystemTheme = true;
      this.setupBrowserThemeDetection();
    }
  }

  /**
   * Setup browser theme detection using prefers-color-scheme
   */
  private setupBrowserThemeDetection(): void {
    if (typeof window === 'undefined') return;

    const isDarkMode = window.matchMedia('(prefers-color-scheme: dark)');
    this.mediaQueryList = isDarkMode;

    // Apply theme based on system preference only if following system theme
    if (this.useSystemTheme) {
      const theme: AppearanceType = isDarkMode.matches ? 'dark' : 'light';
      this.appearance.set(theme);
      this.applyThemeToDOM(theme);
    }

    // Add listener for theme changes and store reference to the handler
    this.handleThemeChange = (event: MediaQueryListEvent) => {
      if (this.useSystemTheme) {
        const newTheme: AppearanceType = event.matches ? 'dark' : 'light';
        this.appearance.set(newTheme);
        this.applyThemeToDOM(newTheme);
      }
    };

    isDarkMode.addEventListener('change', this.handleThemeChange);

    // Store reference for cleanup
    this.mediaQueryList = isDarkMode;
  }

  /**
   * Apply theme class to DOM
   */
  private applyThemeToDOM(theme: AppearanceType): void {

    if (theme === this.currentTheme) {
      if(theme === 'dark') {
        this.renderer.removeClass(this.document.documentElement, `tgui-theme-light`);
      } else {
        this.renderer.removeClass(this.document.documentElement, `tgui-theme-dark`);
      }
      return;
    };

    // Remove existing theme classes
    this.renderer.removeClass(this.document.documentElement, `tgui-theme-${this.currentTheme}`);

    // Add the appropriate theme class
    this.renderer.addClass(this.document.documentElement, `tgui-theme-${theme}`);

    // Update current theme
    this.currentTheme = theme;
  }

  /**
   * Clean up event listeners
   */
  private cleanupListeners(): void {
    // Clean up Telegram listeners
    const telegramData = this.telegramService.getTelegramData();
    if (telegramData && this.themeChangeListener) {
      telegramData.offEvent('themeChanged', this.themeChangeListener);
      this.themeChangeListener = null;
    }

    // Clean up media query listeners using stored reference
    if (this.mediaQueryList && this.handleThemeChange) {
      this.mediaQueryList.removeEventListener('change', this.handleThemeChange);
      this.mediaQueryList = null;
      this.handleThemeChange = null;
    }
  }
}

import { Injectable, inject, Type } from '@angular/core';
import { PlatformService } from '../../../../services/platform.service';
import { TextComponent } from '../../../typography/text/text.component';
import { SubheadlineComponent } from '../../../typography/subheadline/subheadline.component';
import { CaptionComponent } from '../../../typography/caption/caption.component';

/**
 * Service that provides platform-specific typography components for Cell
 * Similar to the React useTypographyCellComponents hook
 */
@Injectable({
  providedIn: 'root'
})
export class TypographyCellService {
  private platformService = inject(PlatformService);

  /**
   * Returns the appropriate component type for the title based on platform
   * iOS: TextComponent
   * Android/Web: SubheadlineComponent with level 1
   */
  getTitleComponent(): Type<any> {
    return this.platformService.isIOS() ? TextComponent : SubheadlineComponent;
  }

  /**
   * Returns the appropriate component type for the description based on platform
   * iOS: CaptionComponent
   * Android/Web: SubheadlineComponent with level 2
   */
  getDescriptionComponent(): Type<any> {
    return this.platformService.isIOS() ? CaptionComponent : SubheadlineComponent;
  }

  /**
   * Returns the level to use for title component if it's a SubheadlineComponent
   */
  getTitleLevel(): string {
    return this.platformService.isIOS() ? '' : '1';
  }

  /**
   * Returns the level to use for description component if it's a SubheadlineComponent
   */
  getDescriptionLevel(): string {
    return '2';
  }
}

