import { Directive, Input } from '@angular/core';

/**
 * A directive for selecting content slots in components.
 * This allows components to project content into specific areas using the content-slot attribute.
 * 
 * @example
 * ```html
 * <div content-slot="header">Header Content</div>
 * ```
 */
@Directive({
  selector: '[content-slot]',
  standalone: true
})
export class ContentSlotDirective {
  @Input('content-slot') slotName!: string;
}

import { Directive, HostBinding } from '@angular/core';

@Directive({
  selector: '[tguiHorizontalScroll]',
  standalone: true
})
export class HorizontalScrollDirective {
  @HostBinding('style.display') display = 'flex';
  @HostBinding('style.overflow-x') overflowX = 'scroll';
  @HostBinding('style.-webkit-overflow-scrolling') webkitOverflowScrolling = 'touch';
  @HostBinding('style.scrollbar-width') scrollbarWidth = 'none';        // Firefox
  @HostBinding('style.-ms-overflow-style') msOverflowStyle = 'none';    // IE/Edge
  @HostBinding('class.tgui-hide-scrollbar') hideScrollbar = true;       // Chrome/Safari
}

import { Directive, Input, TemplateRef, ViewContainerRef, OnInit, OnDestroy, ElementRef, EmbeddedViewRef, inject, effect } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { PortalService } from '../services';

/**
 * Directive that renders content into a portal container
 * Use it to project content outside of its normal DOM hierarchy
 */
@Directive({
  selector: '[tguiPortalOutlet]',
  standalone: true
})
export class PortalOutletDirective implements OnInit, OnDestroy {
  @Input() tguiPortalOutlet: TemplateRef<any> | null = null;

  private destroy$ = new Subject<void>();

  private viewContainerRef = inject(ViewContainerRef);
  private portalService = inject(PortalService);

  constructor() {
    effect(() => {
      const container = this.portalService.portalContainer();

      // Skip rendering if we're not initialized yet
      if (!this.tguiPortalOutlet) return;

      // Clear existing content
      this.viewContainerRef.clear();

      // Only render if we have both a container and a template
      if (container && this.tguiPortalOutlet) {
        const embeddedViewRef = this.viewContainerRef.createEmbeddedView(this.tguiPortalOutlet) as EmbeddedViewRef<any>;

        // Move the generated content to the portal container
        const viewRootNodes = embeddedViewRef.rootNodes || [];
        viewRootNodes.forEach((node: Node) => {
          container.nativeElement.appendChild(node);
        });
      }
    });
  }

  ngOnInit(): void {
    // Initialization is now handled in the effect
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

import { Directive, ElementRef, Input, OnChanges, SimpleChanges } from '@angular/core';
import { TguiIconProps } from '../icon.interface';

/**
 * Директива для автоматической передачи свойств иконки в SVG элемент
 * Позволяет использовать подход, аналогичный {...props} в React
 */
@Directive({
  selector: '[tguiSvgProps]',
  standalone: true
})
export class TguiSvgPropsDirective implements OnChanges {
  @Input('tguiSvgProps') props!: TguiIconProps;

  constructor(private el: ElementRef<SVGElement>) {}

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['props'] && this.props) {
      this.applyProps();
    }
  }

  private applyProps(): void {
    const el = this.el.nativeElement;
    const props = this.props;

    // Применяем базовые атрибуты
    if (props.class) el.setAttribute('class', props.class);
    if (props.id) el.setAttribute('id', props.id);
    if (props.fill) el.setAttribute('fill', props.fill);
    if (props.stroke) el.setAttribute('stroke', props.stroke);
    if (props.width) el.setAttribute('width', props.width.toString());
    if (props.height) el.setAttribute('height', props.height.toString());
    if (props.role) el.setAttribute('role', props.role);
    if (props.tabIndex !== undefined) el.setAttribute('tabindex', props.tabIndex.toString());
    if (props.autoFocus) el.setAttribute('autofocus', 'true');
    if (props.ariaLabel) el.setAttribute('aria-label', props.ariaLabel);
    if (props.title) el.setAttribute('title', props.title);

    // Применяем стили
    if (props.style) {
      const styleStr = Object.entries(props.style)
        .map(([key, value]) => `${this.kebabCase(key)}: ${value}`)
        .join('; ');
      el.setAttribute('style', styleStr);
    }

    // Добавляем обработчики событий
    this.setupEventHandlers(el, props);
  }

  private setupEventHandlers(el: SVGElement, props: TguiIconProps): void {
    // Очищаем старые обработчики
    this.removeEventHandlers(el);

    // Добавляем новые
    if (props.onClick) {
      el.addEventListener('click', (e) => props.onClick?.emit(e as MouseEvent));
    }
    if (props.onFocus) {
      el.addEventListener('focus', (e) => props.onFocus?.emit(e as FocusEvent));
    }
    if (props.onBlur) {
      el.addEventListener('blur', (e) => props.onBlur?.emit(e as FocusEvent));
    }
    if (props.onMouseEnter) {
      el.addEventListener('mouseenter', (e) => props.onMouseEnter?.emit(e as MouseEvent));
    }
    if (props.onMouseLeave) {
      el.addEventListener('mouseleave', (e) => props.onMouseLeave?.emit(e as MouseEvent));
    }
    if (props.onKeyDown) {
      el.addEventListener('keydown', (e) => props.onKeyDown?.emit(e as KeyboardEvent));
    }
    if (props.onPointerDown) {
      el.addEventListener('pointerdown', (e) => props.onPointerDown?.emit(e as PointerEvent));
    }
    if (props.onPointerUp) {
      el.addEventListener('pointerup', (e) => props.onPointerUp?.emit(e as PointerEvent));
    }
  }

  private removeEventHandlers(el: SVGElement): void {
    // Функция-заглушка для удаления обработчиков
    // В реальном коде здесь должны быть removeEventListener для каждого типа события
  }

  private kebabCase(str: string): string {
    return str.replace(/([a-z0-9]|(?=[A-Z]))([A-Z])/g, '$1-$2').toLowerCase();
  }
}

import { Directive, ElementRef, OnInit, Renderer2, inject } from '@angular/core';

/**
 * Directive that automatically loads the TGUI styles
 * This should be applied once on a root element (typically body or app-root)
 */
@Directive({
  selector: '[tguiTheme]',
  standalone: true,
})
export class TGUIThemeDirective implements OnInit {
  // CSS file path - this will be loaded from assets in the actual build
  private stylesPath = 'assets/tgui/styles/variables.css';

  private el = inject(ElementRef);
  private renderer = inject(Renderer2);

  ngOnInit(): void {
    this.loadStyles();
  }

  private loadStyles(): void {
    const head = document.head;
    const link = document.createElement('link');

    link.rel = 'stylesheet';
    link.type = 'text/css';
    link.href = this.stylesPath;

    head.appendChild(link);
  }
}

