styles:
:host {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: none;
  border-radius: 8px;
  text-decoration: none;
  box-sizing: border-box;
  max-inline-size: 100%;
  min-inline-size: 80px;
  position: relative;
  cursor: pointer;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  touch-action: manipulation; /* Optimization for mobile devices */
}

:host.stretched {
  inline-size: 100%;
  flex-grow: 1;
}

:host::after {
  content: '';
  position: absolute;
  inset: 0;
  opacity: 0;
  transition: opacity .15s ease-out;
  background: var(--tgui--bg_color);
  border-radius: inherit;
  pointer-events: none;

}

.button-tappable {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  border-radius: inherit;
}

/* Wrapper for all button content */
.button-content-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: inherit; /* Inherit gap from host element */
  position: relative;
  z-index: 1;
  width: 100%;
  height: 100%;
  padding: var(--tgui--button--padding, 0);
  box-sizing: border-box;
  user-select: none;
  -webkit-user-select: none;
}

.before,
.after {
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
  user-select: none;
  -webkit-user-select: none;
}

.content {
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  z-index: var(--tgui--z-index--simple);
  position: relative; /* To be above ripple */
  user-select: none;
  -webkit-user-select: none;
  -webkit-touch-callout: none; /* Prevents context menu on iOS with long press */
}

.spinner {
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: auto;
  background: inherit;
  border-radius: inherit;
  color: var(--tgui--button--spinner-color);
  z-index: 2; /* Raised above content */
}

/* Size Variants */
:host.size-s {
  height: 36px;
  min-width: 34px;
  gap: 6px;
  border-radius: 20px;
  --tgui--button--padding: 8px 12px;
}

:host.size-m {
  height: 42px;
  min-width: 42px;
  gap: 8px;
  border-radius: 8px;
  --tgui--button--padding: 8px 14px;
}

:host.size-l {
  height: 50px;
  gap: 10px;
  --tgui--button--padding: 10px 20px;
}

/* Mode Variants */
:host.mode-filled {
  --tgui--button--hovered-opacity: .15;
  --tgui--button--spinner-color: var(--tgui--button_text_color);
  --tgui--ripple-color: rgba(255, 255, 255, 0.5);
  color: var(--tgui--button_text_color);
  background: var(--tgui--button_color);
}

:host.mode-bezeled {
  --tgui--button--hovered-opacity: .07;
  --tgui--button--spinner-color: var(--tgui--link_color);
  --tgui--ripple-color: rgba(0, 120, 255, 0.5);
  color: var(--tgui--link_color);
  background: var(--tgui--secondary_fill);
}

:host.mode-plain {
  --tgui--button--hovered-opacity: .03;
  --tgui--button--spinner-color: var(--tgui--plain_foreground);
  --tgui--ripple-color: rgba(0, 120, 255, 0.5);
  color: var(--tgui--link_color);
  background: transparent;
}

:host.mode-gray {
  --tgui--button--hovered-opacity: .5;
  --tgui--button--spinner-color: var(--tgui--plain_foreground);
  --tgui--ripple-color: rgba(0, 0, 0, 0.4);
  color: var(--tgui--plain_foreground);
